fun main() {    // Crear un arreglo de 5 notas    val notas = DoubleArray(5)    // Asignar valores a cada elemento del arreglo    notas[0] = 85.0    notas[1] = 90.5    notas[2] = 78.0    notas[3] = 92.0    notas[4] = 88.5    // Calcular el promedio    var suma = 0.0    for (nota in notas) {        suma += nota    }    val promedio = suma / notas.size    // Imprimir el promedio    println("El promedio de las notas es: $promedio")}